<?php


namespace uncanny_learndash_groups;


/**
 * Class AdminMenu
 * @package uncanny_learndash_groups
 */
class AdminMenu {

	/**
	 * AdminMenu constructor.
	 */
	function __construct() {
		add_action( 'admin_menu', array( $this, 'create_uncanny_groups_page' ), 10 );
	}

	/**
	 *
	 */
	public function create_uncanny_groups_page() {

		$page_title = esc_html__( 'Uncanny Groups for LearnDash', 'uncanny-learndash-groups' );
		$menu_title = esc_html__( 'Uncanny Groups', 'uncanny-learndash-groups' );
		$capability = 'manage_options';
		$menu_slug  = 'uncanny-groups-create-group';
		$icon_url   = 'data:image/svg+xml;base64,';
		$position   = 51;

		// Create the main page
		add_menu_page( $page_title, $menu_title, $capability, $menu_slug, null, $icon_url, $position );
	}

}
